mapper.select:

		select bno, writer, title, regdate, replycnt
		from (
			select rownum rn, bno, title, writer, regdate, replycnt
			from (
				select *
            	from TBL_BOARD
            	order by bno desc
            	)
				where rownum <= #{pageNum} * #{amount}
			)
			where rn > (#{pageNum} -1) * #{amount}

mapper.update:
UPDATE TBL_BOARD
SET
    replycnt = replycnt + #{amount}
WHERE
    BNO = #{bno}
