mapper.select:
select * from ( select * from goods_info_tbl where gno in ( select gno from goods_cat_tbl where
HEALTHBEAUTY = 1 GAME = 1 CULTURE = 1 SHOPPING = 1 SUPPLY = 1 KIDS = 1 DESIGN = 1 FOODS = 1 INTERIOR = 1 POLICY = 1 CHARACTER = 1 EXPERIENCE = 1 COLLABORATION = 1 ENTERTAINMENT = 1
) order by likeCount DESC ) where ROWNUM between 1 and 8


SELECT * FROM (
    SELECT g.*, ROWNUM AS rn
    FROM goods_info_tbl g
    WHERE g.gName LIKE '%' || #{searchText} || '%'
    AND ROWNUM <= #{offset} + #{limit}
) 
WHERE rn > #{offset}
